mapper.select:
select
from t_activity_group where now_state in (1,2) and appmodel_id = #{appmodelId,jdbcType=VARCHAR} and delete_state=0
and activity_coupon_id != #{activityGroupId,jdbcType = INTEGER}

mapper.update:
update t_activity_group set delete_state=1
where activity_group_id in

    #{activityGroupId}

mapper.select:
select

from t_activity_group
where start_date >= #{startDate}
and   end_date <  #{endDate} 
and now_state in(1,2)
and delete_state = 0
and appmodel_id = #{appmodelId}
